import logo from './logo.svg';
import './App.css';

function App() {

  const handleChange = (e) => {



    setSignupData({...signupData,[e.target.name]:e.target.value});
    setLoginData({...loginData,[e.target.name]:e.target.value})
};
  return (
    <div className="App">
      <div className="signup">
                <form onSubmit={handleSignupSubmit}>
                    <label for="chk" aria-hidden="true">Sign up</label>
                    <input type="text" name="firstName" placeholder="First name" value={signupData.firstName} onChange={handleChange} required />
                    <input type="text" name="lastName" placeholder="Last name" value={signupData.lastName} onChange={handleChange} required />
                    <input type="email" name="email" placeholder="Email" value={signupData.email} onChange={handleChange} required />
                    <input type="password" name="password" placeholder="Password" value={signupData.password} onChange={handleChange} required />
                    <input type="password" name="confirmPassword" placeholder="Confirm Password" value={signupData.confirmPassword} onChange={handleChange} required />
                    <button type="submit">Sign up</button>
                </form>
            </div>
            <div className="login">
                <form onSubmit={handleLoginSubmit}>
                    <label for="chk" aria-hidden="true">Login</label>
                    <input type="email" name="email" placeholder="Email" value={loginData.email} onChange={handleChange} required />
                    <input type="password" name="password" placeholder="Password"  value={loginData.password} onChange={handleChange} required />
                    <button type="submit">Login</button>
                </form>
            </div>
    </div>
  );
}

export default App;
