import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [emailerror,setEmailError] =useState();
  const [passworderror,setPasswordError] =useState();
  const [loginData, setLoginData] = useState({ email: '', password: '' });

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value })
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    if (!loginData.email  && !loginData.password == null )
    {
      setEmailError("Fields cannot be empty")
    }
    else if (loginData.email == '')
    {
      setEmailError("Email cannot be empty")
    }
    else if (loginData.password == '')
    {
      setEmailError("Password cannot be empty")
    }

  };
  return (
    <div className="App">
      <div className="login">
        {emailerror && <p data-testid="email_error" className='error'>{emailerror}</p>}
        {passworderror && <p data-testid = "password_error" className='error'>{passworderror}</p>}
        <form onSubmit={handleLoginSubmit}>
          <label for="chk" aria-hidden="true">Login</label>
          <input type="email" name="email" placeholder="Email" value={loginData.email} onChange={handleChange} required />
          <input type="password" name="password" placeholder="Password" value={loginData.password} onChange={handleChange} required />
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

export default App;
