import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [loginData, setLoginData] = useState({ email: '', password: '' });

  const handleChange = (e) => {
    setLoginData({ ...loginData, [e.target.name]: e.target.value })
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    try {
        const response = await axios.post('http://localhost:5005/api/User/login', loginData);
        if (response.status === 200) {
            const token = response.data.token;
            localStorage.setItem('token', token);
            console.log("Token:", token)// Store token in local storage 
            navigate('/homepage');
        } else {
            console.log(response.data);
        }
    } catch (error) {
        console.error('Error:', error);
    }
};
  return (
    <div className="App">
      <div className="login">
        <form onSubmit={handleLoginSubmit}>
          <label for="chk" aria-hidden="true">Login</label>
          <input type="email" name="email" placeholder="Email" value={loginData.email} onChange={handleChange} required />
          <input type="password" name="password" placeholder="Password" value={loginData.password} onChange={handleChange} required />
          <button type="submit">Login</button>
        </form>
      </div>
    </div>
  );
}

export default App;
